def solution(l):
    l.sort(reverse=True, key=abs)
    
    res, pre_negative_num = 1, 0
    for num in l:
        if num==0: break
        if num>0: res*=num
        elif pre_negative_num == 0: pre_negative_num = num
        else:
            res*=num*pre_negative_num
            pre_negative_num = 0
    return res
    

def main():
    tests = [[2,0,2,2,0], [-2,-3,4,-5], [5,]]
    ans = [8, 60, ]
    
    N = len(tests)
    isSuccess = True
    for i in range(N):
        res = solution(tests[i])

        if res!=ans[i]:
            print(tests[i], ans[i], res)
            isSuccess=False
    
    if isSuccess: print("Success!")
            
    

if __name__ == "__main__":
    main()